using System.Threading.Tasks;

namespace MKEncryptor_Core
{
    public class MKRawDataProviderStatic : IMKRawDataProvider
    {
        public string LastSavedJson { get; private set; }

        public Task<string> GetJsonString(string key)
        {
            return Task.Factory.StartNew(() => getJsonStringInternal(key));
        }

        private static string getJsonStringInternal(string key)
        {
            if (MKDataSource.FILES_KEY.Equals(key))
                return
                    @"";

            if (MKDataSource.DIRS_KEY.Equals(key))
                return
                    @"";

            if (MKDataSource.TEXTS_KEY.Equals(key))
                return
                    @"";
            
            if (MKDataSource.DEFAULT_USED_CIPHERS.Equals(key))
                return
                    @"";
            return string.Empty;
        }

        public Task<bool> SaveJsonString(string key, string json)
        {
            LastSavedJson = MKDataSource.DecryptWithDefault(json);
            return Task.FromResult(true);
        }
    }
}
